import React from 'react';
import { Routes, Route, Navigate, Outlet } from 'react-router-dom';
import useAuthStore from '../store/authStore';

// Placeholder Pages - We will create these later
import WelcomePage from '../pages/WelcomePage';
import LoginPage from '../pages/LoginPage';
import PoolListPage from '../pages/PoolListPage';
import CreatePoolPage from '../pages/CreatePoolPage';
import EditPoolPage from '../pages/EditPoolPage';
import UserListPage from '../pages/UserListPage';


// A wrapper for routes that require authentication
const ProtectedRoute = () => {
  const { user } = useAuthStore();
  return user ? <Outlet /> : <Navigate to="/login" replace />;
};

// A wrapper for routes that require ADMIN role
const AdminRoute = () => {
  const { user } = useAuthStore();
  // The user must exist and be an admin.
  // The ProtectedRoute wrapper is implicitly handled by this check.
  return user && user.rol === 'ADMIN' ? <Outlet /> : <Navigate to="/" replace />;
};


const AppRouter = () => {
  return (
    <Routes>
      {/* Public Routes */}
      <Route path="/" element={<WelcomePage />} />
      <Route path="/login" element={<LoginPage />} />

      {/* Protected Routes (for all logged-in users) */}
      <Route element={<ProtectedRoute />}>
        <Route path="/pools" element={<PoolListPage />} />
        <Route path="/users" element={<UserListPage />} />
      </Route>

      {/* Admin Routes */}
      <Route element={<AdminRoute />}>
        <Route path="/pools/create" element={<CreatePoolPage />} />
        <Route path="/pools/edit/:id" element={<EditPoolPage />} />
      </Route>

      {/* Fallback Route */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

export default AppRouter;
