import { create } from "zustand";
import * as poolApi from "../api/poolApi";

const poolStore = create((set) => ({
  pools: [],

  fetchPools: async () => {
    const res = await poolApi.getPools();
    set({ pools: res.data });
  },

  createPool: async (data) => {
    await poolApi.createPool(data);
  },

  updatePool: async (id, data) => {
    await poolApi.updatePool(id, data);
  },

  deletePool: async (id) => {
    await poolApi.deletePool(id);
  }
}));

export default poolStore;
