import { useEffect, useState } from "react";
import { authStore } from "../../store/authStore";
import { getPools } from "../../api/poolApi";

export default function PoolList() {
  const { token } = authStore();
  const [pools, setPools] = useState([]);

  useEffect(() => {
    getPools(token).then(setPools);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl">Piscinas</h1>

      <div className="mt-4">
        {pools.map((pool) => (
          <div key={pool.id} className="border p-3 rounded mb-3">
            <h3 className="font-bold">{pool.nombre}</h3>
            <p>Capacidad: {pool.capacidad}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
