import { useEffect } from "react";
import { Link } from "react-router-dom";
import poolStore from "../../store/poolStore";

export default function PoolList() {
  const pools = poolStore((state) => state.pools);
  const fetchPools = poolStore((state) => state.fetchPools);

   useEffect(() => {
    fetchPools();
  }, [fetchPools]); // ESLint ya no dará warning

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Piscinas</h1>

      {pools.length === 0 ? (
        <p>No hay piscinas registradas</p>
      ) : (
        pools.map((pool) => (
          <div key={pool.id} className="border p-3 rounded mb-3">
            <h3 className="font-bold">{pool.nombre}</h3>
            <p>{pool.descripcion}</p>
          </div>
        ))
      )}
    </div>
  );
}
